  //     address_street: state.address.street,
  //     address_city: state.address.city,
  //     address_zip: state.address.zip
  //   };
  // };

  const makePostInstance = () => {
    return {
      _id: state._id,
      course_name: state.course_name,
      course_instructor_name: state.course_instructor_name,
      _course_schedule: state.schedule.split(","),
      _course_preferences: state.preferences
    };
  };

  const handleSubmit = event => {
    event.preventDefault();
    logInput();
    callDbApi("addresses", makeAddressPostInstance);
    resetState();
  };
  return (
    <div style={{ marginTop: 10 }}>
      <h3>Create Course</h3>
      <p>Address ID: {state.address.id}</p>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Course ID: </label>
          <input
            type="number"
            className="form-control"
            name="id"
            value={state.id}
            onChange={handleInput}
          />
        </div>
        <div className="form-group">
          <label>Name: </label>
          <input
            type="text"
            className="form-control"
            name="name"
            value={state.name}
            onChange={handleInput}
          />
        </div>
        <div className="form-group">
          <label>
            Schedule Key: schedule entry keys as comma separated values{" "}
          </label>
          <input
            type="text"
            className="form-control"
            name="schedule"
            value={state.schedule}
            onChange={handleInput}
          />
        </div>
        <div className="form-group">
          <label>Preferences Key: </label>
          <input
            type="text"
            className="form-control"
            name="preferences"
            value={state.preferences}
            onChange={handleInput}
          />
