import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";

const EditStudent = props => {
  const [state, setState] = useState({
    _id: 0,
    course_name: "",
    course_intructor_name: "",
    _course_schedule: "",
    _course_roster: ""
  });

  const handleInput = event => {
    let field = event.target;
    setState({ ...state, [field.name]: field.value });
  };
  const handleSubmit = async event => {
    event.preventDefault();
    const student = {
      _id: state._id,
      course_name: state.course_name,
      course_intructor_name: state.course_intructor_name,
      _course_schedule: state._course_schedule.split(","),
      _course_roster: state._course_roster.split(",")
    };
    console.log(student);
    await axios
      .post(
        "http://localhost:4000/students/update/" + props.match.params.id,
        student
      )
      .then(res => console.log(res.data));

    props.history.push("/");
  };
  useEffect(() => {
    axios
      .get("http://localhost:4000/students/" + props.match.params.id)
      .then(response => {
        setState({
          _id: response.data.student_id,
          student_name: response.data.student_name,
          course_intructor_name: response.data.course_intructor_name,
          _student_schedule: response.data._student_schedule,
          _student_preferences: response.data._student_preferences
        });
      })
      .catch(error => console.log(error));
  }, []);

  return (
    <div style={{ marginTop: 10 }}>
      <h3>Create Student</h3>
      <p>Address ID: {state.address.id}</p>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Student ID: </label>
          <input
            type="number"
            className="form-control"
            name="id"
            value={state.id}
            onChange={handleInput}
          />
        </div>
        <div className="form-group">
          <label>Name: </label>
          <input
            type="text"
            className="form-control"
            name="name"
            value={state.name}
            onChange={handleInput}
          />
        </div>
        <div className="form-group">
          <label>
            Schedule Key: schedule entry keys as comma separated values{" "}
          </label>
          <input
            type="text"
            className="form-control"
            name="schedule"
            value={state.schedule}
            onChange={handleInput}
          />
        </div>
        <div className="form-group">
          <label>Preferences Key: </label>
          <input
            type="text"
            className="form-control"
            name="preferences"
            value={state.preferences}
            onChange={handleInput}
          />
        </div>
        <h4>Address</h4>
        <CreateAddressNested
          {...state.address}
          submit={getAddress}
          handleInput={handleAddressInput}
        />
        <div className="form-group">
          <input
            type="submit"
            value="Create Student"
            className="btn btn-primary"
          />
        </div>
      </form>
    </div>
  );
};

export default EditStudent;
