import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";

const EditStudyLocation = props => {
  const [state, setState] = useState({
    studyLocation_name: "",
    studyLocation_noise_level: 0,
    studyLocation_availability: 0,
    _studyLocation_address: "",
    _studyLocation_schedule: ""
  });

  const handleInput = event => {
    let field = event.target;
    setState({ ...state, [field.name]: field.value });
  };
  const handleSubmit = async event => {
    event.preventDefault();
    const studyLocation = {
      _id: state._id,
      studyLocation_name: state.studyLocation_name,
      _studyLocation_address: state._studyLocation_address,
      _studyLocation_schedule: state._studyLocation_schedule.split(","),
      _studyLocation_preferences: state._studyLocation_preferences
    };
    console.log(studyLocation);
    await axios
      .post(
        "http://localhost:4000/studyLocations/update/" + props.match.params.id,
        studyLocation
      )
      .then(res => console.log(res.data));

    props.history.push("/");
  };
  useEffect(() => {
    axios
      .get("http://localhost:4000/studyLocations/" + props.match.params.id)
      .then(response => {
        setState({
          _id: response.data._id,
          studyLocation_name: response.data.studyLocation_name,
          _studyLocation_address: response.data._studyLocation_address,
          _studyLocation_schedule: response.data._studyLocation_schedule,
          _studyLocation_preferences: response.data._studyLocation_preferences
        });
      })
      .catch(error => console.log(error));
  }, []);

  return (
    <div style={{ marginTop: 10 }}>
      <h3>Create StudyLocation</h3>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>StudyLocation ID: </label>
          <input
            type="number"
            className="form-control"
            name="_id"
            value={state._id}
            onChange={handleInput}
          />
        </div>
        <div className="form-group">
          <label>Name: </label>
          <input
            type="text"
            className="form-control"
            name="studyLocation_name"
            value={state.studyLocation_name}
            onChange={handleInput}
          />
        </div>
        <div className="form-group">
          <label>Schedule Keys: </label>
          <input
            type="text"
            className="form-control"
            name="_studyLocation_schedule"
            value={state._studyLocation_schedule}
            onChange={handleInput}
          />
        </div>
        <div className="form-group">
          <label>Preferences Key: </label>
          <input
            type="text"
            className="form-control"
            name="_studyLocation_preferences"
            value={state._studyLocation_preferences}
            onChange={handleInput}
          />
        </div>
        <div className="form-group">
          <label>Address Key: </label>
          <input
            type="text"
            className="form-control"
            name="_studyLocation_address"
            value={state._studyLocation_address}
            onChange={handleInput}
          />
        </div>
        <div className="form-group">
          <input
            type="submit"
            value="Update StudyLocation"
            className="btn btn-primary"
          />
        </div>
      </form>
      <Link to={"/address/edit/" + state._studyLocation_address}>
        Edit Address
      </Link>
    </div>
  );
};

export default EditStudyLocation;
