import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";

const Address = props => {
  console.log(props);
  return (
    <table className="table table-striped" style={{ marginTop: 20 }}>
      <thead>
        <tr>
          <th>DB Key</th>
          <th>Name</th>
          <th>Street</th>
          <th>City</th>
          <th>ZIP</th>
          <th>Action</th>
        </tr>
      </thead>
      <tbody>
        <tr>
          <td>{props._id}</td>
          <td>{props.address_name}</td>
          <td>{props.address_street}</td>
          <td>{props.address_city}</td>
          <td>{props.address_zip}</td>
          <td>
            <Link to={"/address/edit/" + props._id}>Edit</Link>
          </td>
        </tr>
      </tbody>
    </table>
  );
};
const EditStudent = props => {
  const [state, setState] = useState({
    student_id: 0,
    student_name: "",
    _student_address: ""
  });

  const handleInput = event => {
    let field = event.target;
    setState({ ...state, [field.name]: field.value });
  };
  const handleSubmit = async event => {
    event.preventDefault();
    const student = {
      student_id: state.student_id,
      student_name: state.student_name,
      _student_address: state._student_address
    };
    console.log(student);
    await axios
      .post(
        "http://localhost:4000/students/update/" + props.match.params.id,
        student
      )
      .then(res => console.log(res.data));

    props.history.push("/");
  };

  const getAddress = () => {
    let data;
    axios
      .get("http://localhost:4000/addresses/")
      .then(response => {
        console.log(response.data[0]);
        data = response.data[0];
        console.log(data);
      })
      .catch(error => console.log(error));
    console.log(data);
    return <Address address={data} />;
  };
  useEffect(() => {
    axios
      .get("http://localhost:4000/students/" + props.match.params.id)
      .then(response => {
        setState({
          student_id: response.data.student_id,
          student_name: response.data.student_name,
          _student_address: response.data._student_address
        });
      })
      .catch(error => console.log(error));
  }, []);

  return (
    <div style={{ marginTop: 10 }}>
      <h3>Create Student</h3>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Student ID: </label>
          <input
            type="number"
            className="form-control"
            name="student_id"
            value={state.student_id}
            onChange={handleInput}
          />
        </div>
        <div className="form-group">
          <label>Name: </label>
          <input
            type="text"
            className="form-control"
            name="student_name"
            value={state.student_name}
            onChange={handleInput}
          />
        </div>
        <div className="form-group">
          <label>Address Key: </label>
          <input
            type="text"
            className="form-control"
            name="_student_address"
            value={state._student_address}
            onChange={handleInput}
          />
        </div>
        <div className="form-group">
          <input
            type="submit"
            value="Update Student"
            className="btn btn-primary"
          />
        </div>
      </form>

      <tbody>{getAddress()}</tbody>

      <Link to={"/address/edit/" + state._student_address}>Edit Address</Link>
    </div>
  );
};

export default EditStudent;
