import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";

const Student = props => (
  <tr>
    <td>{props.student.student_id}</td>
    <td>{props.student.student_name}</td>
    <td>{props.student.student_address}</td>
    <td>
      <Link to={"/edit/" + props.student._id}>Edit</Link>
    </td>
  </tr>
);

const StudentList = () => {
  const [studentList, setStudentList] = useState([]);

  const list = () => {
    return studentList.map(function(currentStudent, i) {
      return <Student student={currentStudent} key={i} />;
    });
  };

  const callDbApi = () => {
    axios
      .get("http://localhost:4000/students")
      .then(res => {
        setStudentList([res.data]);
      })
      .catch(error => console.log(error));
  };

  useEffect(() => {
    // axios
    //   .get("http://localhost:4000/todos/")
    //   .then(response => {
    //     this.setState({ todos: response.data });
    //   })
    //   .catch(function(error) {
    //     console.log(error);
    //   });
    callDbApi();
  }, []);

  return (
    <div>
      <h3>Student List</h3>
      <table className="table table-striped" style={{ marginTop: 20 }}>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Address</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>{list()}</tbody>
      </table>
    </div>
  );
};

export default StudentList;
